mapper.insert:

	INSERT
		INTO FIFA (
			id,
			pw,
			name,
			role
		)
		VALUES (
			#{id},
			#{pw},
			#{name},
			#{role}
		);
		
	
	
		INSERT
			INTO Stadium (
				std_name
			)
			VALUES (
				#{stadium}
			);
		INSERT
			INTO Club (
				name,
				country,
				stadium
			)
			VALUES (
				#{name},
				#{country},
				#{stadium}
			);
	
	
	
	
		INSERT
			INTO Manager (
				name,
				country,
				birth,
				height,
				weight,
				ovr,
				tactic
			)
			VALUES (
				#{name},
				#{country},
				#{birth},
				#{height},
				#{weight},
				#{ovr},
				#{tactic}
			);
		
	
	
	
		INSERT
			INTO Player (
				name,
				country,
				birth,
				height,
				weight,
				ovr,
				position
			)
			VALUES (
				#{name},
				#{country},
				#{birth},
				#{height},
				#{weight},
				#{ovr},
				#{position}
			);
	
	
	
		
		INSERT
			INTO Image (
				img
			)
			VALUES (
				#{img}
			);
		UPDATE
			${role}
		SET
			
				
					emblem
				
				
					profile
				
			
			= LAST_INSERT_ID()
		WHERE
			name = #{name};
			
	
	
		
		INSERT
			INTO Image (
				img
			)
			VALUES (
				#{img1}
			);
		UPDATE
			Stadium
		SET
			std_img = LAST_INSERT_ID()
		WHERE
			std_name = #{stadium};

mapper.select:

	SELECT
		*
	FROM
		${role}
	WHERE
		name = #{name}

SELECT * FROM awardclub a INNER JOIN ( SELECT season, club, MAX(game) maxgame, MAX(win) maxwin, MAX(draw) maxdraw, MAX(lose) maxlose, MAX(point) maxpoint FROM Ranking GROUP BY season, club ) AS r ON a.season = r.season AND a.name = r.club WHERE
player0 = #{name} OR player1 = #{name} OR player2 = #{name} OR player3 = #{name} OR player4 = #{name} OR player5 = #{name} OR player6 = #{name} OR player7 = #{name} OR player8 = #{name} OR player9 = #{name} OR player10 = #{name}
manager = #{name}
name = #{name}
ORDER BY a.season DESC

select.if:
SELECT *, SUM(countaward) countawards FROM ( SELECT *, COUNT(name) countaward FROM ballondor GROUP BY name UNION SELECT *, COUNT(name) countaward FROM goalscorer GROUP BY name UNION SELECT *, COUNT(name) countaward FROM assistprovider GROUP BY name ) AS award INNER JOIN Position p ON award.position = p.position
WHERE type = #{type}
GROUP BY name ORDER BY countawards DESC LIMIT 10

mapper.select:
SELECT MAX(win) win, MAX(draw) draw, MAX(lose) lose, MAX(game) game, MAX(win) / MAX(game) winodds, MAX(draw) / MAX(game) drawodds, MAX(lose) / MAX(game) loseodds, MAX(point) maxpoint, MAX(goalfor) maxgoalfor, MAX(goalagainst) maxgoalagainst FROM Ranking WHERE season = #{season} AND
club = #{club}
club = #{name}
GROUP BY season, club
