mapper.select:

	SELECT
		*
	FROM
		${role}
	WHERE
		club = #{name}

SELECT SUM(game = ( SELECT * FROM ( SELECT MAX(round) FROM Round WHERE season = #{season} ) AS round ) )
= ( SELECT * FROM ( SELECT COUNT(round) FROM Ranking WHERE season = #{season} AND round = #{round} ) AS round )
FROM Ranking WHERE season = #{season} AND round = #{round} AND club = #{name}

mapper.update:
UPDATE Season SET champion = (SELECT * FROM ( SELECT club FROM Ranking WHERE season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season) ORDER BY point DESC LIMIT 1 ) AS champion ), manager = (SELECT * FROM ( SELECT manager FROM Club WHERE name = (SELECT * FROM ( SELECT club FROM Ranking WHERE season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season) ORDER BY point DESC LIMIT 1 ) AS champion ) ) AS manager ), ballondor = (SELECT * FROM ( SELECT player FROM Score WHERE season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season) GROUP BY player ORDER BY AVG(rating) DESC, goal DESC, assist DESC LIMIT 1 ) AS ballondor), goalscorer = (SELECT * FROM ( SELECT player FROM Score WHERE season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season) AND NOT goal = 0 GROUP BY player ORDER BY SUM(goal) DESC, assist DESC, rating DESC LIMIT 1 ) AS goalscorer), assistprovider = (SELECT * FROM ( SELECT player FROM Score WHERE season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season) AND NOT assist = 0 GROUP BY player ORDER BY SUM(assist) DESC, goal DESC, rating DESC LIMIT 1 ) AS assistprovider) WHERE season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season);
UPDATE Club SET history = CONCAT_WS(',', (SELECT * FROM (SELECT history FROM Club WHERE name = #{name}) AS history), #{history} ' Legend League Champion') WHERE name = #{name};
UPDATE Manager SET history = CONCAT_WS(',', (SELECT * FROM (SELECT history FROM Manager WHERE name = #{manager}) AS history), #{history} ' Legend League Champion') WHERE name = #{manager};
UPDATE Player SET history = CONCAT_WS(',', (SELECT * FROM (SELECT history FROM Player WHERE name = #{player${idx}}) AS history), #{history} ' Legend League Champion') WHERE name = #{player${idx}}
UPDATE Player SET history = CONCAT_WS(',', (SELECT * FROM (SELECT history FROM Player WHERE name = (SELECT * FROM ( SELECT player FROM Score WHERE season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season) GROUP BY player ORDER BY AVG(rating) DESC, goal DESC, assist DESC LIMIT 1 ) AS ballondor) ) AS history) , #{history} ' Ballon Dor') WHERE name = (SELECT * FROM ( SELECT player FROM Score WHERE season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season) GROUP BY player ORDER BY AVG(rating) DESC, goal DESC, assist DESC LIMIT 1 ) AS ballondor);
UPDATE Player SET history = CONCAT_WS(',', (SELECT * FROM (SELECT history FROM Player WHERE name = (SELECT * FROM ( SELECT player FROM Score WHERE season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season) AND NOT goal = 0 GROUP BY player ORDER BY SUM(goal) DESC, assist DESC, rating DESC LIMIT 1 ) AS goalscorer) ) AS history) , #{history} ' 득점왕') WHERE name = (SELECT * FROM ( SELECT player FROM Score WHERE season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season) AND NOT goal = 0 GROUP BY player ORDER BY SUM(goal) DESC, assist DESC, rating DESC LIMIT 1 ) AS goalscorer);
UPDATE Player SET history = CONCAT_WS(',', (SELECT * FROM (SELECT history FROM Player WHERE name = (SELECT * FROM ( SELECT player FROM Score WHERE season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season) AND NOT assist = 0 GROUP BY player ORDER BY SUM(assist) DESC, goal DESC, rating DESC LIMIT 1 ) AS assistprovider) ) AS history) , #{history} ' 도움왕') WHERE name = (SELECT * FROM ( SELECT player FROM Score WHERE season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season) AND NOT assist = 0 GROUP BY player ORDER BY SUM(assist) DESC, goal DESC, rating DESC LIMIT 1 ) AS assistprovider);
INSERT INTO Season ( season, roster ) VALUES ( (SELECT * FROM (SELECT MAX(season) FROM Season) AS season)+1, (SELECT * FROM (SELECT roster FROM Season WHERE season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season)) AS roster) );
UPDATE League SET season = (SELECT * FROM (SELECT MAX(season) FROM Season) AS season), round = null WHERE name = 'Legend League'
